INSERT INTO `dbpustaka_do` (`id`, `judul`, `terbit`, `deskripsi`, `cover`, `linkunduh`, `linkbaca`) VALUES (1,'dekadensiotak vol. 1','Maret 2017','Setelah bosan dengan seri Bengkel Kolase, aku membuat seri zine baru. Kuberi nama dekadensiotak. Sesuai dengan nama blog wordpressku. Beberapa postingan blog pun kutaruh di sini sebelum akhirnya kuhapus.','\"img/cover/do1.jpg\"','https://drive.google.com/open?id=1DdOmI3cxs7MamovpuNG2b0TBziI4l0eF','https://issuu.com/dekadensiotak/docs/dekadensiotak_vol_1_df26a55029156d'),(2,'dekadensiotak vol. 2','April 2018','Setahun sudah, barulah edisi kedua hadir. Memang benar-benar seenaknya.','\"img/cover/do2.jpg\"','https://drive.google.com/open?id=1AfFkW1-3vu-tqh67KTFZo-hT5cQ22MXg','https://issuu.com/dekadensiotak/docs/dekadensiotak_vol_2'),(3,'dekadensiotak vol. 3','Juni 2018','bla bla bla','\"img/cover/do3.jpg\"','https://drive.google.com/open?id=1k8WTWk4mXliOviYzpVlbPQnJTWdKNSuM','https://issuu.com/dekadensiotak/docs/dekadensiotak_vol_3');
